import React, { Component } from 'react'

import { initializeApp } from 'firebase/app';
import { getDatabase, ref, set, get,child } from "firebase/database";
import 'react-app-polyfill/ie11';
import './index.css';
import { useGauge } from '../.';
import { useControls } from 'leva';

// TODO: Replace with your app's Firebase project configuration



const App = () => {
  const {
    value,
    diameter,
    minValue,
    maxValue,
    startAngle,
    endAngle,
    numTicks,
  } = useControls('Gauge settings', {
    diameter: { value: 300 },
    value: { value: 0, min: 0, max: 100 },
    minValue: { value: 0 },
    maxValue: { value: 100 },
    startAngle: { value: 90, min: 0, max: 360, step: 1 },
    endAngle: { value: 270, min: 0, max: 360, step: 1 },
    numTicks: { value: 11, min: 0, max: 30, step: 1 },
  });

  const {
    offset,
    strokeWidth: arcStrokeWidth,
    color: progressColor,
    strokeLineCap,
  } = useControls('Arc Props', {
    offset: {
      value: 8,
      min: 0,
      max: 100,
      step: 1,
    },
    strokeWidth: {
      value: 24,
      min: 0,
      max: 100,
    },
    color: {
      value: 'cornflowerblue',
    },
    strokeLineCap: {
      value: 'round',
      options: ['butt', 'round', 'square'],
    },
  });

  const { color: tickColor, length: tickLength } = useControls('Tick Props', {
    color: {
      value: '#ccc',
    },
    length: {
      value: 10,
      min: 0,
      max: 50,
    },
  });

  const {
    baseRadius,
    tipRadius,
    color: needleColor,
  } = useControls('Needle Props', {
    baseRadius: {
      value: 12,
      min: 0,
      max: 50,
    },
    tipRadius: {
      value: 2,
      min: 0,
      max: 50,
    },
    color: {
      value: '#374151',
    },
  });

  const {
    ticks,
    getTickProps,
    getLabelProps,
    valueToAngle,
    angleToValue,
    getArcProps,
    getNeedleProps,
    getSVGProps,
  } = useGauge({
    startAngle,
    endAngle,
    numTicks,
    diameter,
    domain: [minValue, maxValue],
  });

  const { tip, base, points } = getNeedleProps({
    value,
    baseRadius,
    tipRadius,
  });

  return (
    <div className="h-screen flex items-center justify-center">
      <svg {...getSVGProps()} className="max-w-full overflow-visible">
        <path
          {...getArcProps({
            offset,
            startAngle,
            endAngle,
          })}
          fill="none"
          className="stroke-gray-100"
          strokeWidth={arcStrokeWidth}
          // @ts-ignore
          strokeLinecap={strokeLineCap}
        />
        {value > minValue && (
          <path
            {...getArcProps({
              offset,
              startAngle,
              endAngle: valueToAngle(value),
            })}
            fill="none"
            stroke={progressColor}
            strokeWidth={arcStrokeWidth}
            // @ts-ignore
            strokeLinecap={strokeLineCap}
          />
        )}
        <g id="ticks">
          {ticks.map((angle) => {
            return (
              <React.Fragment key={`tick-group-${angle}`}>
                <line
                  stroke={tickColor}
                  {...getTickProps({ angle, length: tickLength })}
                />
                <text
                  className="text-sm fill-gray-500 font-medium"
                  {...getLabelProps({ angle, offset: 20 })}
                >
                  {angleToValue(angle)}
                </text>
              </React.Fragment>
            );
          })}
        </g>
        <g id="needle">
          <circle className="fill-gray-300" {...base} r={24} />
          <circle fill={needleColor} {...base} />
          <circle fill={needleColor} {...tip} />
          <polyline fill={needleColor} points={points} />
          <circle className="fill-white" {...base} r={4} />
        </g>
      </svg>
    </div>
  );
};


const firebaseConfig = {

    apiKey: "AIzaSyCUci--3h7AlnoWyshM4rZbXPoVKzQXC68",
    authDomain: "wirelessnetwork-5f810.firebaseapp.com",
    databaseURL: "https://wirelessnetwork-5f810-default-rtdb.firebaseio.com",
    projectId: "wirelessnetwork-5f810",
    storageBucket: "wirelessnetwork-5f810.appspot.com",
    messagingSenderId: "511134433821",
    appId: "1:511134433821:web:50173d32f84754c911fcf4",
    measurementId: "G-FFEXX3M9C8"

};

const app = initializeApp(firebaseConfig);

// Get a reference to the database service
const database = getDatabase(app);

function writeUserData(userId, name, email, imageUrl) {
  set(ref(database, '/jason/userId'), {
    username: 'name',
    email: "email",
    profile_picture : "imageUrl"
  });
}


export default class getData extends Component {
  state ={
    sensor1: '',
    sensor2: ''
  }

    readSensor1Data =()=>{
      const dbRef = ref(getDatabase());
      get(child(dbRef,'/sensor/sensor1' )).then((snapshot) => {
      if (snapshot.exists()) {
        this.setState ({
          sensor1 : snapshot.val()
        })
      } else {
        console.log("No data available");
      }
    }).catch((error) => {
      console.error(error);
    });
    }
    readSensor2Data =()=>{
      const dbRef = ref(getDatabase());
      get(child(dbRef,'/sensor/sensor2' )).then((snapshot) => {
      if (snapshot.exists()) {
        this.setState ({
          sensor2 : snapshot.val()
        })
      } else {
        console.log("No data available");
      }
    }).catch((error) => {
      console.error(error);
    });
    }

    render() {
      return (
        <div>
          <p>the noise: {this.state.sensor1} db</p>
          <button onClick={this.readSensor1Data}> get data </button>
          <br/>
          <p>the noise: {this.state.sensor2} db</p>
          <button onClick={this.readSensor2Data}> get data </button>
          <App></App>
        </div>
      )
    }
}
